import { useOrganization, useOrganizationList, useUser } from '@clerk/clerk-react';
import React, { useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './GroupPage.css';

const GroupPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useUser();
  const { setActive } = useOrganizationList();
  const { organization, isLoaded, memberships } = useOrganization({
    memberships: {
      infinite: true
    }
  });

  useEffect(() => {
    const setActiveOrganization = async () => {
      try {
        await setActive({ organization: id });
      } catch (error) {
        console.error("Error setting active organization:", error);
      }
    };

    if (id) {
      setActiveOrganization();
    }
  }, [id, setActive]);

  if (!isLoaded) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Loading...</div>
      </div>
    );
  }

  if (!organization) {
    return (
      <div className="error-container">
        <h2>Group not found</h2>
        <p>The group you're looking for doesn't exist or you don't have access to it.</p>
        <button 
          className="action-btn"
          onClick={() => navigate('/create-group')}
        >
          Create New Group
        </button>
      </div>
    );
  }

  const isAdmin = organization.membership?.role === 'admin';
  const pendingInvitations = memberships?.data?.filter(
    member => member.status === 'pending'
  );
  const activeMembers = memberships?.data?.filter(
    member => member.status === 'active'
  );

  const handleRemoveMember = async (memberId) => {
    try {
      const membership = memberships?.data?.find(m => m.id === memberId);
      if (membership) {
        await membership.destroy();
        // Refresh memberships
        memberships?.revalidate();
      }
    } catch (error) {
      console.error("Error removing member:", error);
    }
  };

  return (
    <div className="group-page-container">
      <header className="group-header">
        <div className="group-info">
          <h1>{organization.name}</h1>
          <p className="description">
            {organization.privateMetadata?.description || 'No description provided'}
          </p>
          <div className="group-meta">
            <span>Created: {new Date(organization.createdAt).toLocaleDateString()}</span>
            <span>Members: {activeMembers?.length || 0}</span>
            {organization.maxAllowedMemberships && (
              <span>Max Members: {organization.maxAllowedMemberships}</span>
            )}
          </div>
        </div>
      </header>

      <section className="group-content">
        <div className="members-section">
          <div className="section-header">
            <h2>Active Members ({activeMembers?.length || 0})</h2>
            {isAdmin && (
              <button 
                className="invite-btn"
                onClick={() => navigate('/invite-members')}
              >
                Invite Members
              </button>
            )}
          </div>

          <div className="members-list">
            {activeMembers?.map((membership) => (
              <div key={membership.id} className="member-card">
                <div className="member-info">
                  <img 
                    src={membership.publicUserData?.imageUrl || '/default-avatar.png'} 
                    alt={membership.publicUserData?.identifier || 'Member'} 
                    className="member-avatar"
                  />
                  <div className="member-details">
                    <span className="member-name">
                      {membership.publicUserData?.identifier || 'Unknown Member'}
                      {membership.publicUserData?.userId === user?.id && ' (You)'}
                    </span>
                    <span className="member-role">{membership.role}</span>
                  </div>
                </div>
                <div className="member-actions">
                  <span className="member-status active">Active</span>
                  {isAdmin && membership.publicUserData?.userId !== user?.id && (
                    <button 
                      className="remove-member-btn"
                      onClick={() => handleRemoveMember(membership.id)}
                    >
                      Remove
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>

          {pendingInvitations?.length > 0 && (
            <>
              <h2 className="pending-header">
                Pending Invitations ({pendingInvitations.length})
              </h2>
              <div className="members-list">
                {pendingInvitations.map((membership) => (
                  <div key={membership.id} className="member-card pending">
                    <div className="member-info">
                      <div className="member-details">
                        <span className="member-name">
                          {membership.publicUserData?.identifier || membership.emailAddress}
                        </span>
                        <span className="member-role">Invited</span>
                      </div>
                    </div>
                    <div className="member-actions">
                      <span className="member-status pending">Pending</span>
                      {isAdmin && (
                        <button 
                          className="revoke-invite-btn"
                          onClick={() => handleRemoveMember(membership.id)}
                        >
                          Revoke
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>

        <div className="group-actions">
          <button 
            className="action-btn"
            onClick={() => navigate('/add-bill')}
          >
            Add Bill
          </button>
          <button 
            className="action-btn"
            onClick={() => navigate('/expenses')}
          >
            View Expenses
          </button>
        </div>
      </section>
    </div>
  );
};

export default GroupPage;
