import React, { useState } from 'react';
import './CreateGroup.css';

const CreateGroup = () => {
  const [groupName, setGroupName] = useState('');
  const [members, setMembers] = useState([]);
  const [memberInput, setMemberInput] = useState('');
  const [description, setDescription] = useState('');

  const handleAddMember = () => {
    if (memberInput.trim()) {
      setMembers([...members, memberInput.trim()]);
      setMemberInput('');
    }
  };

  const handleCreateGroup = (e) => {
    e.preventDefault();
    // Here you'll add logic to save the group
    console.log({
      groupName,
      members,
      description
    });
  };

  return (
    <div className="create-group-container">
      <h1>Create a group</h1>
      <p className="subtitle">Start a new group to track bills and IOUs with friends, family, and more.</p>

      <form onSubmit={handleCreateGroup}>
        <div className="input-group">
          <input
            type="text"
            placeholder="Enter a group name"
            value={groupName}
            onChange={(e) => setGroupName(e.target.value)}
            className="group-name-input"
          />
        </div>

        <div className="member-section">
          <div className="input-group">
            <input
              type="text"
              placeholder="Invite via email or phone number"
              value={memberInput}
              onChange={(e) => setMemberInput(e.target.value)}
              className="member-input"
            />
            <button type="button" onClick={handleAddMember} className="add-member-btn">
              +
            </button>
          </div>

          {members.length > 0 && (
            <div className="members-list">
              {members.map((member, index) => (
                <div key={index} className="member-chip">
                  {member}
                  <button
                    type="button"
                    onClick={() => setMembers(members.filter((_, i) => i !== index))}
                    className="remove-member"
                  >
                    Ã—
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>

        <textarea
          placeholder="Tell them why you're adding them to this group..."
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="group-description"
        />

        <button type="submit" className="create-group-btn">
          Create group
        </button>
      </form>
    </div>
  );
};

export default CreateGroup;
