import { useOrganization, useOrganizationList, useUser } from '@clerk/clerk-react';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AddBill.css';

const membershipsParams = {
  memberships: {
    infinite: true,
    includePublicUserData: true
  }
};

const AddBill = () => {
  const navigate = useNavigate();
  const { user } = useUser();
  const { userMemberships, isLoaded: isOrgListLoaded } = useOrganizationList({
    userMemberships: {
      infinite: true
    }
  });
  const { organization, memberships, isLoaded: isOrgMembershipsLoaded } = useOrganization(membershipsParams);

  const [billData, setBillData] = useState({
    description: '',
    amount: '',
    selectedOrg: '',
    paidBy: '',
    date: new Date().toISOString().split('T')[0],
  });

  const [members, setMembers] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch members when organization changes
  useEffect(() => {
    const fetchMembers = async () => {
      if (billData.selectedOrg && userMemberships?.data) {
        try {
          setIsLoading(true);
          setError('');
          const selectedMembership = userMemberships.data.find(
            membership => membership.organization.id === billData.selectedOrg
          );

          if (selectedMembership) {
            const org = selectedMembership.organization;
            // Get active members only
            const membersList = await org.getMemberships();
            const activeMembers = membersList.data.filter(member => member.status === 'active');
            setMembers(activeMembers || []);
          }
        } catch (err) {
          console.error('Error fetching members:', err);
          setError('Failed to load group members');
        } finally {
          setIsLoading(false);
        }
      } else {
        setMembers([]);
      }
    };

    fetchMembers();
  }, [billData.selectedOrg, userMemberships]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Bill Data:', billData);
    // Handle bill submission
  };

  if (!isOrgListLoaded) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Loading groups...</div>
      </div>
    );
  }

  if (!userMemberships?.data || userMemberships.data.length === 0) {
    return (
      <div className="add-bill-container">
        <h1>Add a bill</h1>
        <p className="subtitle">You need to be part of a group to add bills.</p>
        <div className="error-message">Please create or join a group first.</div>
        <button 
          className="action-btn"
          onClick={() => navigate('/create-group')}
          style={{ marginTop: '1rem' }}
        >
          Create New Group
        </button>
      </div>
    );
  }

  const organizations = userMemberships.data.map(membership => membership.organization);

  return (
    <div className="add-bill-container">
      <h1>Add a bill</h1>
      <p className="subtitle">Add a new bill to track expenses</p>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="organization">Select Group</label>
          <select
            id="organization"
            value={billData.selectedOrg}
            onChange={(e) => setBillData({ ...billData, selectedOrg: e.target.value, paidBy: '' })}
            required
            className="select-input"
          >
            <option value="">Select a group</option>
            {organizations.map((org) => (
              <option key={org.id} value={org.id}>
                {org.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <input
            id="description"
            type="text"
            value={billData.description}
            onChange={(e) => setBillData({ ...billData, description: e.target.value })}
            placeholder="Enter bill description"
            required
            className="text-input"
          />
        </div>

        <div className="form-group">
          <label htmlFor="amount">Amount</label>
          <div className="amount-input-container">
            <span className="currency-symbol">$</span>
            <input
              id="amount"
              type="number"
              value={billData.amount}
              onChange={(e) => setBillData({ ...billData, amount: e.target.value })}
              placeholder="0.00"
              required
              min="0"
              step="0.01"
              className="amount-input"
            />
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="paidBy">Who paid?</label>
          <select
            id="paidBy"
            value={billData.paidBy}
            onChange={(e) => setBillData({ ...billData, paidBy: e.target.value })}
            required
            className="select-input"
            disabled={!billData.selectedOrg || isLoading}
          >
            <option value="">
              {isLoading ? 'Loading members...' : 'Select who paid'}
            </option>
            {members.map((member) => {
              const isCurrentUser = member.publicUserData?.userId === user?.id;
              const displayName = isCurrentUser 
                ? 'You'
                : member.publicUserData?.firstName 
                  ? `${member.publicUserData.firstName} ${member.publicUserData.lastName || ''}`
                  : member.publicUserData?.identifier || member.publicUserData?.emailAddress;

              return (
                <option 
                  key={member.id} 
                  value={member.publicUserData?.userId}
                >
                  {displayName}
                </option>
              );
            })}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="date">Date</label>
          <input
            id="date"
            type="date"
            value={billData.date}
            onChange={(e) => setBillData({ ...billData, date: e.target.value })}
            required
            className="date-input"
          />
        </div>

        <button 
          type="submit" 
          className="submit-btn"
          disabled={isLoading}
        >
          {isLoading ? 'Loading...' : 'Add Bill'}
        </button>
      </form>
    </div>
  );
};

export default AddBill;
