import React, { useState } from 'react';
import './CreateGroup.css';

const CreateGroup = () => {
  const [groupName, setGroupName] = useState('');
  const [memberEmail, setMemberEmail] = useState('');
  const [members, setMembers] = useState([]);
  const [description, setDescription] = useState('');

  const handleAddMember = () => {
    if (memberEmail && !members.includes(memberEmail)) {
      setMembers([...members, memberEmail]);
      setMemberEmail('');
    }
  };

  const handleRemoveMember = (emailToRemove) => {
    setMembers(members.filter(email => email !== emailToRemove));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
    console.log({
      groupName,
      members,
      description
    });
  };

  return (
    <div className="create-group-container">
      <h1>Create a group</h1>
      <p className="subtitle">Start a new group to track bills and IOUs with friends, family, and more.</p>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={groupName}
          onChange={(e) => setGroupName(e.target.value)}
          placeholder="Enter a group name"
          className="group-name-input"
        />

        <div className="input-group">
          <input
            type="text"
            value={memberEmail}
            onChange={(e) => setMemberEmail(e.target.value)}
            placeholder="Invite via email or phone number"
          />
          <button
            type="button"
            onClick={handleAddMember}
            className="add-member-btn"
          >
            +
          </button>
        </div>

        {members.length > 0 && (
          <div className="members-list">
            {members.map((email, index) => (
              <div key={index} className="member-chip">
                {email}
                <button
                  type="button"
                  onClick={() => handleRemoveMember(email)}
                  className="remove-member"
                >
                  Ã—
                </button>
              </div>
            ))}
          </div>
        )}

        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Tell them why you're adding them to this group..."
          className="group-description"
        />

        <button type="submit" className="create-group-btn">
          Create group
        </button>
      </form>
    </div>
  );
};

export default CreateGroup;
