import React, { useState } from 'react';
import './CreateGroup.css';
import Navigation from './Navigation';

const CreateGroup = () => {
  const [groupName, setGroupName] = useState('');
  const [members, setMembers] = useState([]);
  const [newMember, setNewMember] = useState('');
  const [description, setDescription] = useState('');

  const handleAddMember = () => {
    if (newMember.trim()) {
      setMembers([...members, newMember.trim()]);
      setNewMember('');
    }
  };

  const handleRemoveMember = (index) => {
    setMembers(members.filter((_, i) => i !== index));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({
      groupName,
      members,
      description
    });
  };

  return (
    <>
      <Navigation />
      <div className="create-group-container">
        <h1>Create a gro</h1>
        <p className="subtitle">Start a new group to track bills and IOUs with friends, family, and more.</p>

        <form onSubmit={handleSubmit}>
          <input
            type="text"
            className="group-name-input"
            placeholder="Enter a group name"
            value={groupName}
            onChange={(e) => setGroupName(e.target.value)}
          />

          <div className="input-group">
            <input
              type="text"
              placeholder="Invite via email or phone number"
              value={newMember}
              onChange={(e) => setNewMember(e.target.value)}
            />
            <button 
              type="button" 
              className="add-member-btn"
              onClick={handleAddMember}
            >
              +
            </button>
          </div>

          {members.length > 0 && (
            <div className="members-list">
              {members.map((member, index) => (
                <div key={index} className="member-chip">
                  {member}
                  <button
                    type="button"
                    className="remove-member"
                    onClick={() => handleRemoveMember(index)}
                  >
                    Ã—
                  </button>
                </div>
              ))}
            </div>
          )}

          <textarea
            className="group-description"
            placeholder="Tell them why you're adding them to this group..."
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />

          <button type="submit" className="create-group-btn">
            Create group
          </button>
        </form>
      </div>
    </>
  );
};

export default CreateGroup;
