import { useOrganizationList, useUser } from '@clerk/clerk-react';
import React from 'react';
import './ExpenseList.css';

const ExpenseList = () => {
  const { isLoaded: isUserLoaded, user } = useUser();
  const { 
    isLoaded: isOrgLoaded, 
    userMemberships 
  } = useOrganizationList({
    userMemberships: {
      infinite: true
    }
  });

  if (!isUserLoaded || !isOrgLoaded) {
    return <div className="p-4 text-gray-600">Loading organizations...</div>;
  }

  if (!userMemberships?.data?.length) {
    return (
      <div className="p-8 text-center bg-gray-50 rounded-lg">
        <p className="text-gray-600 mb-4">You haven't joined any organizations yet.</p>
        <p className="text-gray-600">Create or join an organization to get started!</p>
      </div>
    );
  }

  return (
    <div className="expense-list">
      {userMemberships.data.map((membership) => (
        <div key={membership.organization.id} className="expense-item">
          <div className="expense-info">
            <h3>{membership.organization.name}</h3>
            <p>Your Role: {membership.role}</p>
            <p className="expense-date">
              Joined: {new Date(membership.createdAt).toLocaleDateString()}
            </p>
            <p className="text-sm text-gray-500">
              Members: {membership.organization.membersCount}
            </p>
          </div>
          <div className="text-sm text-gray-600">
            Organization ID: {membership.organization.id}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ExpenseList;
