import { useOrganization, useOrganizationList, useUser } from '@clerk/clerk-react';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AddBill.css';

const userMembershipsParams = {
  userMemberships: {
    infinite: true,
    keepPreviousData: true,
  },
};

const orgMembersParams = {
  memberships: {
    infinite: true,
    includePublicUserData: true,
  },
};

const AddBill = () => {
  const navigate = useNavigate();
  const { user } = useUser();
  const { isLoaded: isOrgListLoaded, userMemberships } = useOrganizationList(userMembershipsParams);
  const [selectedOrgId, setSelectedOrgId] = useState('');

  const [billData, setBillData] = useState({
    description: '',
    amount: '',
    selectedOrg: '',
    paidBy: '',
    date: new Date().toISOString().split('T')[0],
  });

  // Get organization details and members for the selected organization
  const { organization, memberships, isLoaded: isOrgMembershipsLoaded } = useOrganization({
    ...orgMembersParams,
    organizationId: selectedOrgId || undefined
  });

  // Debug logging
  useEffect(() => {
    if (isOrgMembershipsLoaded && memberships?.data) {
      console.log('Raw Members Data:', memberships.data);
      console.log('Member Statuses:', memberships.data.map(m => ({ 
        status: m.status, 
        role: m.role,
        id: m.publicUserData.userId,
        name: m.publicUserData.identifier
      })));
    }
  }, [isOrgMembershipsLoaded, memberships?.data]);

  // Handle organization selection
  const handleGroupChange = (e) => {
    const newOrgId = e.target.value;
    setSelectedOrgId(newOrgId);
    setBillData(prev => ({ ...prev, selectedOrg: newOrgId, paidBy: '' }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Bill Data:', billData);
    // Handle bill submission
  };

  // Show loading state while fetching organizations
  if (!isOrgListLoaded) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Loading your groups...</div>
      </div>
    );
  }

  // Show message if no organizations data is available
  if (!userMemberships?.data) {
    return (
      <div className="add-bill-container">
        <h1>Add a bill</h1>
        <p className="subtitle">Loading your groups...</p>
      </div>
    );
  }

  // Get list of organizations where user is a member
  const organizations = userMemberships.data.map(membership => ({
    id: membership.organization.id,
    name: membership.organization.name,
    role: membership.role,
    createdAt: new Date(membership.organization.createdAt).toLocaleDateString(),
    members: membership.organization.membersCount || 0
  }));

  // If user has no organizations
  if (organizations.length === 0) {
    return (
      <div className="add-bill-container">
        <h1>Add a bill</h1>
        <p className="subtitle">You need to be part of a group to add bills.</p>
        <div className="error-message">Please create or join a group first.</div>
        <button 
          className="action-btn"
          onClick={() => navigate('/create-group')}
          style={{ marginTop: '1rem' }}
        >
          Create New Group
        </button>
      </div>
    );
  }

  // Get active members for the selected organization
  const activeMembers = memberships?.data
    ?.filter(member => {
      // Debug log for each member
      console.log('Member:', {
        id: member.publicUserData.userId,
        status: member.status,
        role: member.role,
        name: member.publicUserData.identifier
      });
      // Include members that have any role
      return member.role !== null && member.role !== undefined;
    })
    .map(member => {
      const userData = member.publicUserData;
      const displayName = userData.firstName && userData.lastName 
        ? `${userData.firstName} ${userData.lastName}`
        : userData.identifier || userData.emailAddress || 'Unknown Member';

      return {
        id: userData.userId,
        name: displayName,
        role: member.role,
        joinedAt: new Date(member.createdAt).toLocaleDateString(),
        isCurrentUser: userData.userId === user?.id
      };
    }) || [];

  // Log processed members
  useEffect(() => {
    console.log('Processed Active Members:', activeMembers);
  }, [activeMembers]);

  return (
    <div className="add-bill-container">
      <h1>Add a bill</h1>
      <p className="subtitle">Add a new bill to track expenses</p>

      <div className="organizations-list">
        <h2>Your Groups</h2>
        {organizations.map((org) => (
          <div key={org.id} className="organization-item">
            <div className="org-header">
              <h3>{org.name}</h3>
              <span className="org-role">{org.role}</span>
            </div>
            <div className="org-details">
              <p>Created: {org.createdAt}</p>
              <p>Members: {org.members}</p>
            </div>
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="organization">Select Group</label>
          <select
            id="organization"
            value={billData.selectedOrg}
            onChange={handleGroupChange}
            required
            className="select-input"
          >
            <option value="">Select a group</option>
            {organizations.map((org) => (
              <option key={org.id} value={org.id}>
                {org.name} ({org.role})
              </option>
            ))}
          </select>
        </div>

        {selectedOrgId && (
          <>
            <div className="members-list">
              <h3>Group Members</h3>
              {activeMembers.map((member) => (
                <div key={member.id} className="member-item">
                  <span className="member-name">
                    {member.isCurrentUser ? 'You' : member.name}
                  </span>
                  <span className="member-role">{member.role}</span>
                  <span className="member-joined">Joined: {member.joinedAt}</span>
                </div>
              ))}
            </div>

            <div className="form-group">
              <label htmlFor="paidBy">Who paid?</label>
              <select
                id="paidBy"
                value={billData.paidBy}
                onChange={(e) => setBillData(prev => ({ ...prev, paidBy: e.target.value }))}
                required
                className="select-input"
                disabled={!isOrgMembershipsLoaded || !activeMembers.length}
              >
                <option value="">
                  {!selectedOrgId 
                    ? 'Select a group first'
                    : !isOrgMembershipsLoaded 
                      ? 'Loading members...' 
                      : 'Select who paid'}
                </option>
                {activeMembers.map((member) => (
                  <option key={member.id} value={member.id}>
                    {member.isCurrentUser ? 'You' : member.name} ({member.role})
                  </option>
                ))}
              </select>
              {selectedOrgId && !activeMembers.length && isOrgMembershipsLoaded && (
                <div className="error-message" style={{ marginTop: '0.5rem' }}>
                  No active members found in this group
                </div>
              )}
            </div>
          </>
        )}

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <input
            type="text"
            id="description"
            value={billData.description}
            onChange={(e) => setBillData(prev => ({ ...prev, description: e.target.value }))}
            required
            className="text-input"
            placeholder="What was this expense for?"
          />
        </div>

        <div className="form-group">
          <label htmlFor="amount">Amount</label>
          <input
            type="number"
            id="amount"
            value={billData.amount}
            onChange={(e) => setBillData(prev => ({ ...prev, amount: e.target.value }))}
            required
            className="text-input"
            placeholder="How much was spent?"
            min="0"
            step="0.01"
          />
        </div>

        <div className="form-group">
          <label htmlFor="date">Date</label>
          <input
            type="date"
            id="date"
            value={billData.date}
            onChange={(e) => setBillData(prev => ({ ...prev, date: e.target.value }))}
            required
            className="date-input"
          />
        </div>

        <button 
          type="submit" 
          className="submit-btn"
          disabled={!isOrgMembershipsLoaded || !selectedOrgId || !activeMembers.length}
        >
          Add Bill
        </button>
      </form>
    </div>
  );
};

export default AddBill;
