import { useOrganization, useOrganizationList, useUser } from '@clerk/clerk-react';
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './GroupDetails.css';

// Profile Icon Component
const ProfileIcon = ({ firstName, lastName }) => {
  const initials = `${firstName?.[0] || ''}${lastName?.[0] || ''}`.toUpperCase();
  return (
    <div className="profile-icon">
      {initials}
    </div>
  );
};

const GroupDetails = () => {
  const { orgId } = useParams();
  const navigate = useNavigate();
  const { user } = useUser();
  const { setActive } = useOrganizationList();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expenses, setExpenses] = useState([]);

  const { organization, isLoaded: isOrgLoaded, memberships } = useOrganization({
    organizationId: orgId,
    memberships: {
      infinite: true,
      includePublicUserData: true
    }
  });

  useEffect(() => {
    const initializeGroup = async () => {
      try {
        if (!orgId) return;

        // Set the active organization
        await setActive({ organization: orgId });

        // Fetch expenses for the group
        const idPart = orgId.includes('/') ? orgId.split('/').pop() : orgId;
        const cleanOrgId = idPart.replace(/^org_/, '').replace(/[^a-zA-Z0-9]/g, '');

        const response = await fetch(`http://localhost:3005/api/groups/${cleanOrgId}/member-details`);

        if (!response.ok) {
          throw new Error('Failed to fetch group expenses');
        }

        const data = await response.json();
        setExpenses(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error('Error initializing group:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (orgId) {
      initializeGroup();
    }
  }, [orgId, setActive]);

  if (!isOrgLoaded || loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Loading group details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
      </div>
    );
  }

  if (!organization) {
    return (
      <div className="error-container">
        <h2>Group not found</h2>
        <p>The group you're looking for doesn't exist or you don't have access to it.</p>
        <button 
          className="action-btn"
          onClick={() => navigate('/expenses')}
        >
          Back to Groups
        </button>
      </div>
    );
  }

  const activeMembers = memberships?.data?.filter(member => member.status === 'active') || [];
  const createdDate = new Date(organization.createdAt).toLocaleDateString();

  return (
    <div className="group-page-container">
      <header className="group-header">
        <h1>{organization.name}</h1>
        <p className="description">
          {organization.privateMetadata?.description || 'No description provided'}
        </p>
        <div className="group-meta">
          <span>Created: {createdDate}</span>
          <span>Active Members: {activeMembers.length}</span>
        </div>
      </header>

      <section className="members-section">
        <h2>Members ({activeMembers.length})</h2>
        <div className="members-list">
          {activeMembers.map((member) => (
            <div key={member.id} className="member-card">
              <div className="member-info">
                <img 
                  src={member.publicUserData?.imageUrl || '/default-avatar.png'} 
                  alt={member.publicUserData?.identifier || 'Member'} 
                  className="member-avatar"
                />
                <div className="member-details">
                  <span className="member-name">
                    {member.publicUserData?.identifier}
                    {member.publicUserData?.userId === user?.id && ' (You)'}
                  </span>
                  <span className="member-role">{member.role}</span>
                </div>
              </div>
              <span className="member-status active">Active</span>
            </div>
          ))}
        </div>
      </section>

      <section className="members-section">
        <div className="section-header">
          <h2>Group Expenses</h2>
          <div className="expense-stats">
            <span>Total Expenses: {expenses.length}</span>
          </div>
        </div>

        {expenses.length === 0 ? (
          <div className="no-expenses">
            <p>No expenses found for this group.</p>
            <button 
              className="action-btn"
              onClick={() => navigate('/add-bill')}
            >
              Add First Expense
            </button>
          </div>
        ) : (
          <div className="members-grid">
            {expenses.map((expense) => {
              const payer = activeMembers.find(m => m.publicUserData.userId === expense.paid_by_user_id);
              const firstName = payer?.publicUserData?.firstName || '';
              const lastName = payer?.publicUserData?.lastName || '';
              const payerName = payer ? 
                `${firstName} ${lastName}`.trim() || payer.publicUserData.identifier : 
                'Unknown';

              return (
                <div key={expense.id} className="member-card">
                  <div className="member-info">
                    <ProfileIcon firstName={firstName} lastName={lastName} />
                    <div className="member-details">
                      <h3>{expense.description}</h3>
                      <div className="member-meta">
                        <span className="member-role">â‚¹{parseFloat(expense.amount).toFixed(2)}</span>
                        <span className="member-joined">{new Date(expense.date).toLocaleDateString()}</span>
                      </div>
                      <span className="member-email">Paid by: {payerName}</span>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </section>

      <div className="group-actions">
        <button 
          className="action-btn"
          onClick={() => navigate('/add-bill')}
        >
          Add Bill
        </button>
        <button 
          className="action-btn"
          onClick={() => navigate('/expenses')}
        >
          Back to Groups
        </button>
      </div>
    </div>
  );
};

export default GroupDetails;
