import { useOrganization } from '@clerk/clerk-react';
import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './GroupPage.css';

const GroupPage = () => {
  const { organizationId } = useParams();
  const { organization, isLoaded } = useOrganization(organizationId);
  const navigate = useNavigate();

  if (!isLoaded) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Loading...</div>
      </div>
    );
  }

  if (!organization) {
    return (
      <div className="error-container">
        <h2>Group not found</h2>
        <button 
          className="action-btn"
          onClick={() => navigate('/create-group')}
        >
          Create New Group
        </button>
      </div>
    );
  }

  return (
    <div className="group-page-container">
      <header className="group-header">
        <h1>{organization.name}</h1>
        <p className="description">
          {organization.privateMetadata?.description || 'No description provided'}
        </p>
      </header>

      <section className="group-content">
        <div className="members-section">
          <h2>Group Members</h2>
          <div className="members-list">
            {organization.memberships?.map((membership) => (
              <div key={membership.id} className="member-card">
                <div className="member-info">
                  <img 
                    src={membership.publicUserData?.imageUrl || '/default-avatar.png'} 
                    alt={membership.publicUserData?.identifier || 'Member'} 
                    className="member-avatar"
                  />
                  <div className="member-details">
                    <span className="member-name">
                      {membership.publicUserData?.identifier || 'Unknown Member'}
                    </span>
                    <span className="member-role">{membership.role}</span>
                  </div>
                </div>
                <span className="member-status">
                  {membership.status === 'active' ? 'Active' : 'Pending'}
                </span>
              </div>
            ))}
          </div>
        </div>

        <div className="group-actions">
          <button 
            className="action-btn"
            onClick={() => navigate('/add-bill')}
          >
            Add Bill
          </button>
          <button 
            className="action-btn"
            onClick={() => navigate('/expenses')}
          >
            View Expenses
          </button>
        </div>
      </section>
    </div>
  );
};

export default GroupPage;
