import { useOrganization, useUser } from '@clerk/clerk-react';
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './ExpenseList.css';

// Profile Icon Component
const ProfileIcon = ({ firstName, lastName }) => {
  const initials = `${firstName?.[0] || ''}${lastName?.[0] || ''}`.toUpperCase();
  return (
    <div className="profile-icon">
      {initials}
    </div>
  );
};
const GroupDetails = () => {
  const { orgId } = useParams();
  const navigate = useNavigate();

  const { 
    organization, 
    isLoaded: isOrgLoaded 
  } = useOrganization({ organizationId: orgId });

  const { user, isLoaded: isUserLoaded } = useUser();
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      if (!organization || !user) {
        return;
      }

      try {
        setLoading(true);

        const apiUrl = `http://localhost:3005/api/groups/${orgId}/member-details`;
        const response = await fetch(apiUrl);

        if (!response.ok) {
          throw new Error(`Failed to fetch member details: ${response.status} ${response.statusText}`);
        }

        const data = await response.json();
        setExpenses(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (isOrgLoaded && isUserLoaded && orgId) {
      fetchData();
    }
  }, [organization, user, isOrgLoaded, isUserLoaded, orgId]);

  if (loading) {
    return <div className="expense-list-container">Loading group details...</div>;
  }

  if (error) {
    return (
      <div className="expense-list-container">
        <h1>Error</h1>
        <p className="subtitle">{error}</p>
      </div>
    );
  }

  const isAdmin = organization?.membership?.role === 'admin';

  return (
    <div className="expense-list-container">
      <h1>{organization?.name || 'Loading...'}</h1>

      <div className="group-info">
        <p className="subtitle">Active Members: {organization?.memberships?.data?.length || 0}</p>
        <p className="subtitle">Pending Invites: {organization?.invitations?.data?.length || 0}</p>

        {isAdmin && (
          <button 
            className="delete-group-btn"
            onClick={() => navigate('/expenses')}
          >
            Delete Group
          </button>
        )}
      </div>

      <div className="expense-list">
        <div className="section-header">
          <h2>Member Expenses</h2>
          <div className="expense-stats">
            <span>Total Expenses: {expenses.length}</span>
          </div>
        </div>

        {expenses.length === 0 ? (
          <div className="no-expenses">
            <p className="subtitle">No expenses found for this group.</p>
          </div>
        ) : (
          <div className="expenses-grid">
            {expenses.map((expense) => (
              <div key={expense.id} className="expense-card">
                <div className="expense-info">
                  <div className="expense-header">
                    <ProfileIcon firstName="John" lastName="Doe" />
                    <div className="expense-details">
                      <h3>{expense.description}</h3>
                      <div className="expense-meta">
                        <span className="expense-amount">₹{parseFloat(expense.amount).toFixed(2)}</span>
                        <span className="expense-date">{new Date(expense.date).toLocaleDateString()}</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default GroupDetails;
