import { useOrganization, useOrganizationList, useUser } from '@clerk/clerk-react';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AddBill.css';

const AddBill = () => {
  const navigate = useNavigate();
  const { user } = useUser();
  const { userMemberships, isLoaded: isOrgListLoaded } = useOrganizationList();
  const [selectedOrgId, setSelectedOrgId] = useState('');

  const [billData, setBillData] = useState({
    description: '',
    amount: '',
    selectedOrg: '',
    paidBy: '',
    date: new Date().toISOString().split('T')[0],
  });

  // Get organization details and members for the selected organization
  const { organization, memberships, isLoaded: isOrgMembershipsLoaded } = useOrganization({
    memberships: {
      infinite: true,
      includePublicUserData: true
    },
    organizationId: selectedOrgId
  });

  // Handle organization selection
  const handleGroupChange = (e) => {
    const newOrgId = e.target.value;
    setSelectedOrgId(newOrgId);
    setBillData(prev => ({ ...prev, selectedOrg: newOrgId, paidBy: '' }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Bill Data:', billData);
    // Handle bill submission
  };

  // Show loading state while fetching organizations
  if (!isOrgListLoaded) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Loading groups...</div>
      </div>
    );
  }

  // Show message if no organizations data
  if (!userMemberships?.data) {
    return (
      <div className="add-bill-container">
        <h1>Add a bill</h1>
        <p className="subtitle">Loading your groups...</p>
        <div className="loading-spinner">Please wait...</div>
      </div>
    );
  }

  // Show create group option if no organizations
  if (userMemberships.data.length === 0) {
    return (
      <div className="add-bill-container">
        <h1>Add a bill</h1>
        <p className="subtitle">You need to be part of a group to add bills.</p>
        <div className="error-message">Please create or join a group first.</div>
        <button 
          className="action-btn"
          onClick={() => navigate('/create-group')}
          style={{ marginTop: '1rem' }}
        >
          Create New Group
        </button>
      </div>
    );
  }

  const organizations = userMemberships.data.map(membership => membership.organization);
  const activeMembers = memberships?.data?.filter(member => member.status === 'active') || [];

  return (
    <div className="add-bill-container">
      <h1>Add a bill</h1>
      <p className="subtitle">Add a new bill to track expenses</p>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="organization">Select Group</label>
          <select
            id="organization"
            value={billData.selectedOrg}
            onChange={handleGroupChange}
            required
            className="select-input"
          >
            <option value="">Select a group</option>
            {organizations.map((org) => (
              <option key={org.id} value={org.id}>
                {org.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <input
            id="description"
            type="text"
            value={billData.description}
            onChange={(e) => setBillData({ ...billData, description: e.target.value })}
            placeholder="Enter bill description"
            required
            className="text-input"
          />
        </div>

        <div className="form-group">
          <label htmlFor="amount">Amount</label>
          <div className="amount-input-container">
            <span className="currency-symbol">$</span>
            <input
              id="amount"
              type="number"
              value={billData.amount}
              onChange={(e) => setBillData({ ...billData, amount: e.target.value })}
              placeholder="0.00"
              required
              min="0"
              step="0.01"
              className="amount-input"
            />
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="paidBy">Who paid?</label>
          <select
            id="paidBy"
            value={billData.paidBy}
            onChange={(e) => setBillData({ ...billData, paidBy: e.target.value })}
            required
            className="select-input"
            disabled={!selectedOrgId || !isOrgMembershipsLoaded}
          >
            <option value="">
              {!selectedOrgId 
                ? 'Select a group first'
                : !isOrgMembershipsLoaded || !memberships?.data
                  ? 'Loading members...' 
                  : 'Select who paid'}
            </option>
            {activeMembers.map((member) => {
              const isCurrentUser = member.publicUserData?.userId === user?.id;
              let displayName;

              if (isCurrentUser) {
                displayName = 'You';
              } else {
                const userData = member.publicUserData;
                if (userData?.firstName && userData?.lastName) {
                  displayName = `${userData.firstName} ${userData.lastName}`;
                } else if (userData?.firstName) {
                  displayName = userData.firstName;
                } else if (userData?.identifier) {
                  displayName = userData.identifier;
                } else {
                  displayName = userData?.emailAddress || 'Unknown Member';
                }
              }

              return (
                <option 
                  key={member.id} 
                  value={member.publicUserData?.userId}
                >
                  {displayName}
                </option>
              );
            })}
          </select>
          {selectedOrgId && !activeMembers.length && isOrgMembershipsLoaded && (
            <div className="error-message" style={{ marginTop: '0.5rem' }}>
              No active members found in this group
            </div>
          )}
        </div>

        <div className="form-group">
          <label htmlFor="date">Date</label>
          <input
            id="date"
            type="date"
            value={billData.date}
            onChange={(e) => setBillData({ ...billData, date: e.target.value })}
            required
            className="date-input"
          />
        </div>

        <button 
          type="submit" 
          className="submit-btn"
          disabled={!isOrgMembershipsLoaded || !selectedOrgId || !activeMembers.length}
        >
          {!selectedOrgId 
            ? 'Select a group first'
            : !isOrgMembershipsLoaded 
              ? 'Loading...' 
              : !activeMembers.length
                ? 'No members available'
                : 'Add Bill'}
        </button>
      </form>
    </div>
  );
};

export default AddBill;
