import React, { useState } from 'react';
import './AddBill.css';
import Navigation from './Navigation';

const AddBill = () => {
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [splitType, setSplitType] = useState('everyone'); // everyone, specific, evenly

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({
      amount,
      description,
      splitType
    });
  };

  return (
    <div>
      <Navigation />
      <div className="add-bill-container">
        <h1>Add a bill</h1>
        <p className="subtitle">Splitting a bill? Add your share and we'll do the math for you</p>

        <form onSubmit={handleSubmit}>
          <div className="bill-section">
            <h2>Bill details</h2>

            <div className="form-group">
              <label>Amount</label>
              <div className="amount-input">
                <span className="currency">â‚¹</span>
                <input
                  type="number"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  placeholder="0.00"
                  step="0.01"
                />
              </div>
            </div>

            <div className="form-group">
              <label>Description</label>
              <input
                type="text"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                placeholder="What was this for?"
              />
            </div>
          </div>

          <div className="split-section">
            <h2>Who should split this?</h2>
            <div className="split-options">
              <button
                type="button"
                className={`split-option ${splitType === 'everyone' ? 'active' : ''}`}
                onClick={() => setSplitType('everyone')}
              >
                Everyone
              </button>
              <button
                type="button"
                className={`split-option ${splitType === 'specific' ? 'active' : ''}`}
                onClick={() => setSplitType('specific')}
              >
                Specific people
              </button>
              <button
                type="button"
                className={`split-option ${splitType === 'evenly' ? 'active' : ''}`}
                onClick={() => setSplitType('evenly')}
              >
                Evenly
              </button>
            </div>
          </div>

          <div className="button-group">
            <button type="button" className="cancel-btn">
              Cancel
            </button>
            <button type="submit" className="save-btn">
              Save
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddBill;
