import { useOrganizationList, useUser } from '@clerk/clerk-react';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CreateGroup.css';

const CreateGroup = () => {
  const { createOrganization } = useOrganizationList();
  const { user } = useUser();
  const navigate = useNavigate();

  const [groupName, setGroupName] = useState('');
  const [memberEmail, setMemberEmail] = useState('');
  const [members, setMembers] = useState([]);
  const [description, setDescription] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleAddMember = () => {
    if (memberEmail && !members.includes(memberEmail)) {
      setMembers([...members, memberEmail]);
      setMemberEmail('');
    }
  };

  const handleRemoveMember = (emailToRemove) => {
    setMembers(members.filter(email => email !== emailToRemove));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      // Create the organization using Clerk
      const organization = await createOrganization({
        name: groupName,
        // You can also add private metadata for the description
        privateMetadata: {
          description: description
        }
      });

      // Invite members to the organization
      if (organization) {
        const invitePromises = members.map(async (email) => {
          try {
            await organization.inviteMember({
              emailAddress: email,
              role: 'basic_member'
            });
          } catch (inviteError) {
            console.error(`Failed to invite ${email}:`, inviteError);
            return `Failed to invite ${email}`;
          }
        });

        await Promise.all(invitePromises);

        // Navigate to the expenses page or organization page
        navigate('/expenses');
      }
    } catch (err) {
      console.error('Error creating group:', err);
      setError('Failed to create group. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="create-group-container">
      <h1>Create a group</h1>
      <p className="subtitle">Start a new group to track bills and IOUs with friends, family, and more.</p>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={groupName}
          onChange={(e) => setGroupName(e.target.value)}
          placeholder="Enter a group name"
          className="group-name-input"
          required
        />

        <div className="input-group">
          <input
            type="email"
            value={memberEmail}
            onChange={(e) => setMemberEmail(e.target.value)}
            placeholder="Invite via email"
            className="member-input"
          />
          <button
            type="button"
            onClick={handleAddMember}
            className="add-member-btn"
            disabled={!memberEmail}
          >
            +
          </button>
        </div>

        {members.length > 0 && (
          <div className="members-list">
            {members.map((email, index) => (
              <div key={index} className="member-chip">
                {email}
                <button
                  type="button"
                  onClick={() => handleRemoveMember(email)}
                  className="remove-member"
                >
                  Ã—
                </button>
              </div>
            ))}
          </div>
        )}

        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Tell them why you're adding them to this group..."
          className="group-description"
        />

        <button 
          type="submit" 
          className="create-group-btn"
          disabled={isLoading || !groupName}
        >
          {isLoading ? 'Creating group...' : 'Create group'}
        </button>
      </form>
    </div>
  );
};

export default CreateGroup;
