import { useOrganization, useOrganizationList, useUser } from '@clerk/clerk-react';
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './GroupPage.css';

const GroupPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useUser();
  const { setActive } = useOrganizationList();
  const { organization, isLoaded, memberships } = useOrganization({
    memberships: {
      infinite: true,
      includePublicUserData: true
    }
  });

  const [memberDetails, setMemberDetails] = useState([]);

  useEffect(() => {
    const setActiveOrganization = async () => {
      try {
        await setActive({ organization: id });
      } catch (error) {
        console.error("Error setting active organization:", error);
      }
    };

    if (id) {
      setActiveOrganization();
    }
  }, [id, setActive]);

  useEffect(() => {
    if (memberships?.data) {
      const details = memberships.data.map(member => ({
        id: member.id,
        userId: member.publicUserData?.userId,
        email: member.publicUserData?.identifier || member.emailAddress,
        name: member.publicUserData?.firstName 
          ? `${member.publicUserData.firstName} ${member.publicUserData.lastName || ''}`
          : member.publicUserData?.identifier,
        imageUrl: member.publicUserData?.imageUrl,
        role: member.role,
        status: member.status,
        joinedAt: new Date(member.createdAt).toLocaleDateString()
      }));
      setMemberDetails(details);
    }
  }, [memberships?.data]);

  if (!isLoaded) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Loading...</div>
      </div>
    );
  }

  if (!organization) {
    return (
      <div className="error-container">
        <h2>Group not found</h2>
        <p>The group you're looking for doesn't exist or you don't have access to it.</p>
        <button 
          className="action-btn"
          onClick={() => navigate('/create-group')}
        >
          Create New Group
        </button>
      </div>
    );
  }

  const isAdmin = organization.membership?.role === 'admin';
  const activeMembers = memberDetails.filter(member => member.status === 'active');
  const pendingMembers = memberDetails.filter(member => member.status === 'pending');

  const handleRemoveMember = async (memberId) => {
    try {
      const membership = memberships?.data?.find(m => m.id === memberId);
      if (membership) {
        await membership.destroy();
        memberships?.revalidate();
      }
    } catch (error) {
      console.error("Error removing member:", error);
    }
  };

  return (
    <div className="group-page-container">
      <header className="group-header">
        <div className="group-info">
          <h1>{organization.name}</h1>
          <p className="description">
            {organization.privateMetadata?.description || 'No description provided'}
          </p>
          <div className="group-meta">
            <span>Created: {new Date(organization.createdAt).toLocaleDateString()}</span>
            <span>Active Members: {activeMembers.length}</span>
            <span>Pending Invites: {pendingMembers.length}</span>
          </div>
        </div>
      </header>

      <section className="group-content">
        <div className="members-section">
          <div className="section-header">
            <h2>Members ({activeMembers.length})</h2>
            {isAdmin && (
              <button 
                className="invite-btn"
                onClick={() => navigate('/create-group')}
              >
                Invite Members
              </button>
            )}
          </div>

          <div className="members-grid">
            {activeMembers.map((member) => (
              <div key={member.id} className="member-card">
                <div className="member-info">
                  <img 
                    src={member.imageUrl || '/default-avatar.png'} 
                    alt={member.name || 'Member'} 
                    className="member-avatar"
                  />
                  <div className="member-details">
                    <span className="member-name">
                      {member.name}
                      {member.userId === user?.id && ' (You)'}
                    </span>
                    <span className="member-email">{member.email}</span>
                    <div className="member-meta">
                      <span className="member-role">{member.role}</span>
                      <span className="member-joined">Joined {member.joinedAt}</span>
                    </div>
                  </div>
                </div>
                <div className="member-actions">
                  <span className="member-status active">Active</span>
                  {isAdmin && member.userId !== user?.id && (
                    <button 
                      className="remove-member-btn"
                      onClick={() => handleRemoveMember(member.id)}
                    >
                      Remove
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>

          {pendingMembers.length > 0 && (
            <>
              <h2 className="pending-header">
                Pending Invitations ({pendingMembers.length})
              </h2>
              <div className="members-grid">
                {pendingMembers.map((member) => (
                  <div key={member.id} className="member-card pending">
                    <div className="member-info">
                      <div className="member-details">
                        <span className="member-name">{member.email}</span>
                        <span className="member-meta">
                          <span className="invite-date">Invited {member.joinedAt}</span>
                        </span>
                      </div>
                    </div>
                    <div className="member-actions">
                      <span className="member-status pending">Pending</span>
                      {isAdmin && (
                        <button 
                          className="revoke-invite-btn"
                          onClick={() => handleRemoveMember(member.id)}
                        >
                          Revoke
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>

        <div className="group-actions">
          <button 
            className="action-btn"
            onClick={() => navigate('/add-bill')}
          >
            Add Bill
          </button>
          <button 
            className="action-btn"
            onClick={() => navigate('/expenses')}
          >
            View Expenses
          </button>
        </div>
      </section>
    </div>
  );
};

export default GroupPage;
