import React, { useEffect, useState } from 'react';
import { useOrganization, useUser } from '@clerk/clerk-react';
import './ExpenseList.css';

const ExpenseList = () => {
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { organization } = useOrganization();
  const { user } = useUser();

  useEffect(() => {
    const fetchExpenses = async () => {
      if (!organization?.id) return;

      try {
        const response = await fetch(`http://localhost:3001/api/expenses?organizationId=${organization.id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch expenses');
        }
        const data = await response.json();
        setExpenses(data);
      } catch (err) {
        console.error('Error fetching expenses:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchExpenses();
  }, [organization?.id]);

  if (loading) {
    return <div className="p-4 text-gray-600">Loading expenses...</div>;
  }

  if (error) {
    return <div className="p-4 text-red-600">Error: {error}</div>;
  }

  if (!expenses.length) {
    return (
      <div className="p-8 text-center bg-gray-50 rounded-lg">
        <p className="text-gray-600 mb-4">No expenses found for this group.</p>
        <p className="text-gray-600">Add your first expense to get started!</p>
      </div>
    );
  }

  return (
    <div className="expense-list">
      {expenses.map((expense) => (
        <div key={expense.id} className="expense-item">
          <div className="expense-info">
            <h3>{expense.description}</h3>
            <p>Paid by {expense.paid_by_user_id === user?.id ? 'You' : expense.paid_by_user_id}</p>
            <p className="expense-date">{new Date(expense.date).toLocaleDateString()}</p>
          </div>
          <div className="expense-amount">
            ${parseFloat(expense.amount).toFixed(2)}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ExpenseList;
