import { useOrganization, useUser } from '@clerk/clerk-react';
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './ExpenseList.css';

const ProfileIcon = ({ firstName, lastName }) => {
  const initials = `${firstName?.[0] || ''}${lastName?.[0] || ''}`.toUpperCase();
  return (
    <div className="profile-icon">
      {initials}
    </div>
  );
};

const GroupDetails = () => {
  const { orgId } = useParams();
  const navigate = useNavigate();

  const { 
    organization, 
    isLoaded: isOrgLoaded,
    memberships,
    invitations
  } = useOrganization({ 
    organizationId: orgId,
    memberships: { infinite: true },
    invitations: { infinite: true }
  });

  const { user, isLoaded: isUserLoaded } = useUser();
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [groupDetails, setGroupDetails] = useState(null);

  // Fetch both group details and expenses
  useEffect(() => {
    const fetchGroupData = async () => {
      if (!user) return;

      try {
        setLoading(true);
        const idPart = orgId.includes('/') ? orgId.split('/').pop() : orgId;
        const cleanOrgId = idPart.replace(/^org_/, '').replace(/[^a-zA-Z0-9]/g, '');

        // First fetch group details
        const groupResponse = await fetch(`http://localhost:3005/api/groups/${cleanOrgId}`);
        if (!groupResponse.ok) {
          throw new Error(`Failed to fetch group details: ${groupResponse.status}`);
        }
        const groupData = await groupResponse.json();
        setGroupDetails(groupData);

        // Then fetch expenses
        const expensesResponse = await fetch(`http://localhost:3005/api/groups/${cleanOrgId}/member-details`);
        if (!expensesResponse.ok) {
          throw new Error(`Failed to fetch member details: ${expensesResponse.status}`);
        }
        const expensesData = await expensesResponse.json();

        if (!Array.isArray(expensesData)) {
          throw new Error('Received invalid data format from server');
        }

        setExpenses(expensesData);
      } catch (err) {
        console.error('Error fetching data:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (isUserLoaded && orgId) {
      fetchGroupData();
    }
  }, [orgId, user, isUserLoaded]);

  const handleDeleteGroup = async () => {
    if (!window.confirm('Are you sure you want to delete this group? This action cannot be undone.')) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:3005/api/groups/${orgId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'user-id': user.id
        }
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to delete group');
      }

      navigate('/expenses');
    } catch (err) {
      console.error('Error deleting group:', err);
      setError(err.message);
    }
  };

  if (loading) {
    return <div className="expense-list-container">Loading group details...</div>;
  }

  if (error) {
    return (
      <div className="expense-list-container">
        <h1>Error</h1>
        <p className="subtitle">{error}</p>
      </div>
    );
  }

  if (!groupDetails) {
    return (
      <div className="expense-list-container">
        <h1>Error</h1>
        <p className="subtitle">Group not found</p>
      </div>
    );
  }

  const isAdmin = organization?.membership?.role === 'admin';

  return (
    <div className="expense-list-container">
      <h1>{groupDetails.name}</h1>

      <div className="group-info">
        <p className="subtitle">Active Members: {memberships?.data?.length || 0}</p>
        <p className="subtitle">Pending Invites: {invitations?.data?.length || 0}</p>

        {isAdmin && (
          <button 
            className="delete-group-btn"
            onClick={handleDeleteGroup}
          >
            Delete Group
          </button>
        )}
      </div>

      <div className="expense-list">
        <div className="section-header">
          <h2>Member Expenses</h2>
          <div className="expense-stats">
            <span>Total Expenses: {expenses.length}</span>
          </div>
        </div>

        {expenses.length === 0 ? (
          <div className="no-expenses">
            <p className="subtitle">No expenses found for this group.</p>
            <button 
              className="action-btn"
              onClick={() => navigate('/add-bill')}
            >
              Add First Expense
            </button>
          </div>
        ) : (
          <div className="expenses-grid">
            {expenses.map((expense) => {
              const payer = memberships?.data?.find(m => m.publicUserData.userId === expense.paid_by_user_id);
              const firstName = payer?.publicUserData?.firstName || '';
              const lastName = payer?.publicUserData?.lastName || '';
              const payerName = payer ? 
                `${firstName} ${lastName}` : 
                'Unknown';

              return (
                <div key={expense.id} className="expense-card">
                  <div className="expense-info">
                    <div className="expense-header">
                      <ProfileIcon firstName={firstName} lastName={lastName} />
                      <div className="expense-details">
                        <h3>{expense.description}</h3>
                        <div className="expense-meta">
                          <span className="expense-amount">â‚¹{parseFloat(expense.amount).toFixed(2)}</span>
                          <span className="expense-date">{new Date(expense.date).toLocaleDateString()}</span>
                        </div>
                        <p className="payer-info">To be paid by: {payerName}</p>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default GroupDetails;
